import jwt from 'jsonwebtoken';

import{
    SIGN_IN_FAILURE,
    SIGN_IN_REQUEST,
    SIGN_IN_SUCCESS,
    SIGN_UP_FAILURE,
    SIGN_UP_REQUEST,
    SIGN_UP_SUCCESS,
    SIGN_OUT_FAILURE,
    SIGN_OUT_REQUEST,
    SIGN_OUT_SUCCESS,

    FETCH_MOVIES_SUCCESS,
    FETCH_MOVIES_REQUEST,
    FETCH_MOVIES_FAILURE,

    CREATE_MOVIES_SUCCESS,
    CREATE_MOVIES_FAILURE,
    CREATE_MOVIES_REQUEST
} from './action-types';

export const isValidToken = (token) =>{
    let decoded = jwt.decode(token);
    return new Date(decoded.exp * 1000) > new Date() ? decoded : null;
};



const iniState ={
    currentUser: localStorage.getItem("USER-TOKEN")
        ? isValidToken(localStorage.getItem("USER-TOKEN"))
        : null,
    token: localStorage.getItem("USER-TOKEN")
        ? localStorage.getItem("USER-TOKEN")
        : null,
    error:"",
    loading: false,
    isAuthenticated: false,
    movieData:[],
   createMovieData:[],
   isloading:true,
};


export const fetchMoviesReducer = (state=iniState,action) =>{
    switch(action.type){
        case FETCH_MOVIES_REQUEST:
            return{
                ...state,
                loading:false,

            };
        case FETCH_MOVIES_SUCCESS:
            console.log("reducer",action.payload.movieData);
            return{
                ...state,
                loading:false,
                error:null,
                isloading:false,
                movieData:action.payload.movieData
            };
        case FETCH_MOVIES_FAILURE:
            return{
                ...state,
                loading:false,
                error: action.payload.error
            };
            default:
                return{...state};
    }
}


// Create Movie

export const createMoviesReducer = (state=iniState,action) =>{
    switch(action.type){
        case CREATE_MOVIES_REQUEST:
            return{
                ...state,
                loading:false,

            };
        case CREATE_MOVIES_SUCCESS:

            return{
                ...state,
                loading:false,
                isloading:action.payload.loading,
                error:null,
                createMovieData:action.payload.createMovieData
            };
        case CREATE_MOVIES_FAILURE:
            return{
                ...state,
                loading:false,
                error: action.payload.error
            };
            default:
                return{...state};
    }
}


export const authenticationReducer = (state=iniState,action) =>{
    switch(action.type){
        case SIGN_IN_REQUEST:
        case SIGN_UP_REQUEST:
        case SIGN_OUT_REQUEST:
            return{
                ...state,
                loading: true,
                isAuthenticated:false,
            };
        case SIGN_IN_FAILURE:
        case SIGN_UP_FAILURE:
        case SIGN_OUT_FAILURE:
            return {
                ...state,
                loading: false,
                error: action.payload,
                currentUser: null,
                isAuthenticated: false,
            };
        case SIGN_UP_SUCCESS:
        case SIGN_IN_SUCCESS:
            return{
                ...state,
                loading:false,
                token: action.payload.token,
                currentUser: action.payload.user,
                isAuthenticated: true,
            };
        case SIGN_OUT_SUCCESS:
            localStorage.removeItem("USER-TOKEN");
            return{
                ...state,
                isAuthenticated:false,
                loading:false,
                currentUser:null,
                token:"",
            };
            default:
                return{state};
    }
};



export default authenticationReducer;
