import React, { useEffect } from "react";
import { Button } from "../components/styled/Button.style";
import { useDispatch, useSelector } from "react-redux";
import {useForm} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { createMovie } from "../redux/action-creators";
import Home from "./Home";
import { Table } from "../components/Table";

const CreateMovie = () => {

  const dispatch = useDispatch();

  const { createMovieData, error  } = useSelector((state) => state.createMoviesReducer);

  const schema = yup.object().shape({
    title: yup.string().required(),
    year: yup.number().required(),
    rating: yup.number().required(),
    description: yup.string().required()
  })

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = (d) =>{
    // console.log(d);

     dispatch(createMovie(d));
  }





  return (
    <div className="mycard">
      <div className="card auth-card input-field">
      <form onSubmit={handleSubmit(onSubmit)}>
          <label> Title
        <input
         {...register("title")}
        />
        <p>{errors.title?.message}</p>
        {console.log("CREATE ERROS",createMovieData)}
        {createMovieData && createMovieData.movieMessageExists &&
           <p> 
             {createMovieData.movieMessageExists}
           </p>

            }


        </label>
        <label> Year
        <input
        {...register("year")}
        />
         <p>{errors.year?.message}</p>
         </label>
         <label> Rating
         <input
          {...register("rating")}
        />
           <p>{errors.rating?.message}</p>
           </label>
           <label> Description
           <input
          {...register("description")}
        />
           <p>{errors.description?.message}</p>
           </label>
        {error !== null ? <div className="error">{error}</div> : null}
        <input
          className="btn waves-effect waves-light #42a5f5 blue lighten-1"
          type='submit'
          value='Create Movie'
        />
        </form>
      </div>
      <Home/>
      <Table>
  <Table.Head>
    <Table.TR>
      <Table.TH>Title</Table.TH>
      <Table.TH>Year</Table.TH>
      <Table.TH>Rating</Table.TH>
      <Table.TH>Description</Table.TH>
    </Table.TR>
  </Table.Head>
  <Table.Body>
    <Table.TR>
      <Table.TH>data 1</Table.TH>
      <Table.TH>data 2</Table.TH>
    </Table.TR>
    <Table.TR>
      <Table.TH>data 3</Table.TH>
      <Table.TH>data 4</Table.TH>
    </Table.TR>
  </Table.Body>
</Table>
    </div>
  );
};

export default CreateMovie;
