import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {useForm} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { signUp } from "../redux/action-creators";

const Signup = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const { error } = useSelector((state) => state.authenticationReducer);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password_confirm, setPassword_Confirm] = useState("");

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };
  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };
  const handlePassword_Confirm_Change = (event) => {
    setPassword_Confirm(event.target.value);
  };

  const handleSignUpClick = (event) => {
    event.preventDefault();
    dispatch(signUp({ email, password,password_confirm }, history));
  };

  return (
    <div className="mycard">
      <div className="card auth-card input-field">
        <input
          type="text"
          placeholder="email"
          value={email}
          onChange={handleEmailChange}
        />
        <input
          type="password"
          placeholder="password"
          value={password}
          onChange={handlePasswordChange}
        />
         <input
          type="password_confirm"
          placeholder="password_confirm"
          value={password_confirm}
          onChange={handlePassword_Confirm_Change}
        />
        {error !== null ? <div className="error">{error}</div> : null}
        <button
          className="btn waves-effect waves-light #42a5f5 blue lighten-1"
          onClick={handleSignUpClick}
        >
          SignUP
        </button>
        <h5>
          <Link to="/signin">Already have an account?</Link>
        </h5>
      </div>
    </div>
  );
};

export default Signup;
