                </Table.TH>

 <Table.TH > <StyledButton buttonLabel="WHAT"  backgroundColorOnHover="#01bf41"
 onClick={()=>{
  dispatch(fetchOneMovie({id:post._id}))


 }

        } >EDIT</StyledButton></Table.TH>        


    </Table.TR>          
          ))}
  </Table.Body>
</Table>

