import React from "react";

import { useDispatch, useSelector } from "react-redux";
import {useForm} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { createMovie } from "../redux/action-creators";
import { container,row,col25,col75,InputFormTxt,labelForm,InputFormSub } from "../components/styled/Form.style";
import Home from "./Home";


const CreateMovie = () => {

  const dispatch = useDispatch();

  const { createMovieData, error  } = useSelector((state) => state.createMoviesReducer);

  const schema = yup.object().shape({
    title: yup.string().required(),
    year: yup.number().min(1000).max(9999).typeError("Must be a number"),
    rating: yup.number().min(1).max(10).typeError("Must be a number"),
    description: yup.string().required()
  })

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = (d) =>{
    // console.log(d);

     dispatch(createMovie(d));
     reset();

  }





  return (
    <div className="mycard">

<container>
  <form action="/action_page.php">
    <div class="row">
      <div class="col-25">
        <label for="fname">First Name</label>
      </div>
      <div class="col-75">
        <input type="text" id="fname" name="firstname" placeholder="Your name.."/>
      </div>
    </div>
    <div class="row">
      <div class="col-25">
        <label for="lname">Last Name</label>
      </div>
      <div class="col-75">
        <input type="text" id="lname" name="lastname" placeholder="Your last name.."/>
      </div>
    </div>
    <div class="row">
      <div class="col-25">
        <label for="country">Country</label>
      </div>
      <div class="col-75">
        <select id="country" name="country">
          <option value="australia">Australia</option>
          <option value="canada">Canada</option>
          <option value="usa">USA</option>
        </select>
      </div>
    </div>
    <div class="row">
      <div class="col-25">
        <label for="subject">Subject</label>
      </div>
      <div class="col-75">
        <textarea id="subject" name="subject" placeholder="Write something.." style="height:200px"></textarea>
      </div>
    </div>
    <div class="row">
      <input type="submit" value="Submit"/>
    </div>
  </form>
</container>

      <div className="card auth-card input-field">
      <form onSubmit={handleSubmit(onSubmit)}>
          <label> Title
        <input
         {...register("title")}
        />
        <p>{errors.title?.message}</p>
        {console.log("CREATE ERROS",createMovieData)}
        {createMovieData && createMovieData.movieMessageExists &&
           <p> 
             {createMovieData.movieMessageExists}
           </p>

            }  

        </label>
        <label> Year
        <input
        {...register("year")}
        />
         <p>{errors.year?.message}</p>
         </label>
         <label> Rating
         <input
          {...register("rating")}
        />
        {createMovieData && createMovieData.messageRest &&
           <p> 
             {createMovieData.messageRest}
           </p>
        }   
           <p>{errors.rating?.message}</p>
           </label>
           <label> Description
           <textarea
          {...register("description")}
        />
           <p>{errors.description?.message}</p>
           </label>
        {error !== null ? <div className="error">{error}</div> : null}
        <input
          className="btn waves-effect waves-light #42a5f5 blue lighten-1"
          type='submit'
          value='Create Movie'
        />
        </form>
      </div>
      <Home/>

    </div>
  );
};

export default CreateMovie;
