import React from "react";
import { Switch, Route, Redirect } from 'react-router-dom';
import NavBar from "./Navbar";
import Signin from "../pages/Signin";
import Home from "../pages/Home";
import { useSelector } from "react-redux";



export default function Routes(){
    const { currentUser } = useSelector((state) => state.authenticationReducer
      );

    return (
        <>




                    { currentUser && <NavBar currentUser={currentUser} />}
                    <div className="dashboardContainer">

                        <Switch>
                        <PrivateRoute path="/" component={Home} exact currentUser={currentUser}/>


                            <LoginRoute path="/signin" component={Signin} currentUser={currentUser} />


                        </Switch>
                    </div>


        </>
    )
}
function LoginRoute({ path, component: Component, currentUser, ...props }) {
    return (
        <Route
            {...props}
            path={path}
            render={(props) =>
                currentUser ? <Redirect to="/" /> : <Component {...props} />
            }
        />
    );
}
function PrivateRoute({ path, component: Component, currentUser, ...props }) {
    return (
        <Route
            {...props}
            path={path}
            render={(props) =>
                currentUser ? <Component {...props} /> : <Redirect to="/signin" />
            }
        />
    );
}
