import React, { useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import {fetchMovie,deleteMovie,toggleEdit,updateMovie,fetchOneMovie} from '../redux/action-creators';
import { useHistory } from "react-router";
import { StyledButton}  from "../components/styled/Button.style";
import { Table } from "../components/Table";
import { Input } from "../components/styled/Input.style";
import {useForm,useFieldArray, Controller} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';

const EditMovie = () => {

  const dispatch = useDispatch();
  const history = useHistory();
  const  { movieData } = useSelector((state) =>  state.fetchMoviesReducer);
  const  {movieDataOne}  = useSelector((state) =>  state.fetchOneMoviesReducer);

  const   edit  = useSelector((state) =>  state.toggleEditreducer);

  const schema = yup.object().shape({
    id: yup.string(),
    title: yup.string().required(),
    year: yup.number().min(1000).max(9999).typeError("Must be a number"),
    rating: yup.number().min(1).max(10).typeError("Must be a number"),
    description: yup.string(),
  })

  const {
    register,
    handleSubmit,
    formState: { errors ,isSubmitSuccessful},
    reset,
  } = useForm({    
    resolver: yupResolver(schema),
    defaultValues: { something: "anything" }
  },   
  );

  const onSubmit =  (d) =>{
    console.log("d",d.id);
     dispatch(updateMovie(d));
     dispatch(toggleEdit({id:d.id}));



  }

  // useEffect(async () => {
  //   const result = await fetch('./api/formValues.json'); 
  //   reset(result); 
  //   dispatch( fetchMovie());
  // }, [reset,dispatch])


  useEffect( () => {
    if (formState.isSubmitSuccessful) {
      reset({ something: '' });
    }
   dispatch( fetchMovie());


}, [dispatch,formState, reset]);

console.log("MOVIE ON DATA",movieDataOne)

  return (

    <div>


{edit ? <Table>
  <Table.Head>
    <Table.TR>
      <Table.TH>Title</Table.TH>
      <Table.TH>Year</Table.TH>
      <Table.TH>Rating</Table.TH>
      <Table.TH>Description</Table.TH>
      <Table.TH>Edit</Table.TH>
      <Table.TH>Delete</Table.TH>

    </Table.TR>
  </Table.Head>
  <Table.Body>
  {
    movieData && movieData.map(post => (
      <Table.TR key={post._id}>
      <Table.TH >{post.title}</Table.TH>
      <Table.TH>{post.year}</Table.TH>
      <Table.TH>{post.rating}</Table.TH>
      <Table.TH>{post.description}</Table.TH>
      <Table.TH > <StyledButton buttonLabel="✓" backgroundColor="#01bf41"  backgroundColorOnHover="green"
                        onClick={()=>{

                          // dispatch(fetchOneMovie({id:post._id}))  
                          dispatch(toggleEdit({id:post._id}))

                        }
                  } >EDIT</StyledButton></Table.TH>   
      <Table.TH> <StyledButton buttonLabel="X"  backgroundColor="#e50d0d" 
              onClick={() =>{ dispatch(deleteMovie({id:post._id}))}}>{""}
                </StyledButton> 
                </Table.TH>

    </Table.TR>          
          ))}
  </Table.Body>
</Table>

