import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {useForm} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { signUp } from "../redux/action-creators";

const Signup = () => {

  const dispatch = useDispatch();
  const history = useHistory();
  const { error } = useSelector((state) => state.authenticationReducer);

  const schema = yup.object().shape({
    email: yup.string().email().required(),
    password: yup.string().min(8).max(8).required(),
    password_confirm: yup.string().min(8).max(8).required()
  })

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = (d) =>{
    console.log(d);
     dispatch(signUp(d, history));
  }


  return (
    <div className="mycard">
      <div className="card auth-card input-field">
      <form onSubmit={handleSubmit(onSubmit)}>
      <label>Email</label>
        <input
         {...register("email")}
        />

        <p>{errors.email?.message}</p>
        <label>Email</label>
        <input
        {...register("password")}
        />
         <p>{errors.password?.message}</p>
         <input
          {...register("password_confirm")}
        />
           <p>{errors.password_confirm?.message}</p>
        {error !== null ? <div className="error">{error}</div> : null}
        <input
          className="btn waves-effect waves-light #42a5f5 blue lighten-1"
          type='submit'
          value='Sign Up'
        />
        <h5>
          <Link to="/signin">Already have an account?</Link>
        </h5>
        </form>
      </div>
    </div>
  );
};

export default Signup;
