import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {useForm} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { createMovie } from "../redux/action-creators";

const CreateMovie = () => {

  const dispatch = useDispatch();

  const { CreateMovie, loading, error  } = useSelector((state) => state.createMoviesReducer);

  const schema = yup.object().shape({
    title: yup.string().required(),
    year: yup.number().required(),
    rating: yup.number().required(),
    description: yup.string().required()
  })

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = (d) =>{
    console.log(d);
     dispatch(createMovie(d));
  }





  return (
    <div className="mycard">
      <div className="card auth-card input-field">
      <form onSubmit={handleSubmit(onSubmit)}>
          <label> Title
        <input
         {...register("title")}
        />
        <p>{errors.title?.message}</p>
        <p>{error && error.message}</p>
        </label>
        <label> Year
        <input
        {...register("year")}
        />
         <p>{errors.year?.message}</p>
         </label>
         <label> Rating
         <input
          {...register("rating")}
        />
           <p>{errors.rating?.message}</p>
           </label>
           <label> Description
           <input
          {...register("description")}
        />
           <p>{errors.description?.message}</p>
           </label>
        {error !== null ? <div className="error">{error}</div> : null}
        <input
          className="btn waves-effect waves-light #42a5f5 blue lighten-1"
          type='submit'
          value='Create Movie'
        />
        </form>
      </div>
    </div>
  );
};

export default CreateMovie;
