import React, { useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import {fetchMovie,deleteMovie,toggleEdit} from '../redux/action-creators'
import { StyledButton}  from "../components/styled/Button.style";
import { Table } from "../components/Table";
import { Input } from "../components/styled/Input.style";

const DeleteMovie = () => {

  const dispatch = useDispatch();

  const  { movieData } = useSelector((state) =>  state.fetchMoviesReducer);

  const   edit  = useSelector((state) =>  state.toggleEditreducer);

  useEffect(() => {

   dispatch( fetchMovie());

}, [dispatch]);

  return (

    <div>


{edit ? <Table>
  <Table.Head>
    <Table.TR>
      <Table.TH>Title</Table.TH>
      <Table.TH>Year</Table.TH>
      <Table.TH>Rating</Table.TH>
      <Table.TH>Description</Table.TH>
      <Table.TH>Delete</Table.TH>

    </Table.TR>
  </Table.Head>
  <Table.Body>
  {
    movieData && movieData.map(post => (
      <Table.TR key={post._id}>
      <Table.TH >{post.title}</Table.TH>
      <Table.TH>{post.year}</Table.TH>
      <Table.TH>{post.rating}</Table.TH>
      <Table.TH>{post.description}</Table.TH>
      <Table.TH> <StyledButton buttonLabel="X"  backgroundColor="#e50d0d" 
              onClick={() =>{ dispatch(deleteMovie({id:post._id}))}}>{""}
                </StyledButton> 
                </Table.TH>        


    </Table.TR>          
          ))}
  </Table.Body>
</Table>
:
<Table>
<Table.Head>
  <Table.TR>
    <Table.TH>Title</Table.TH>
    <Table.TH>Year</Table.TH>
    <Table.TH>Rating</Table.TH>
    <Table.TH>Description</Table.TH>
    <Table.TH>Delete</Table.TH>

  </Table.TR>
</Table.Head>
<Table.Body>
{
  movieData && movieData.map(post => (
    <Table.TR key={post._id}>
       <Table.TH> <Input placeholder={post.title}/></Table.TH>
       <Table.TH> <Input placeholder={post.year}/></Table.TH>
       <Table.TH> <Input placeholder={post.rating}/></Table.TH>
       <Table.TH> <Input defaultValue={post.description}/></Table.TH>
    <Table.TH> <StyledButton buttonLabel="X"  backgroundColor="#e50d0d" 
            onClick={() =>{ dispatch(deleteMovie({id:post._id}))}}>{""}
              </StyledButton> 
              </Table.TH>        


  </Table.TR>          
        ))}
</Table.Body>
</Table>
}

<StyledButton buttonLabel="✅"  backgroundColorOnHover="#01bf41" onClick={()=>
          dispatch(toggleEdit())
        }>EDIT</StyledButton>
    </div>
  )
}
export default DeleteMovie
