import styled from 'styled-components';

export const Button = styled.button`
width: 200px;
height: 50px;
background-color:${(props)=>props.backgroundColor};
&:hover {
    background-color:yellow;
    & label {
        color:black
    }
}
`
export const Buttonlaybel = styled.label`
font-size:25px;
color:white

`
