import React, { useEffect,useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import {fetchMovie,deleteMovie} from '../redux/action-creators'
import { StyledButton}  from "../components/styled/Button.style";
import { Table } from "../components/Table";

const DeleteMovie = () => {

  const dispatch = useDispatch();

  const  { movieData, loading, error } = useSelector((state) =>  state.fetchMoviesReducer);

  useEffect(() => {

   dispatch( fetchMovie());

}, [dispatch]);

  return (

    <div>
 <Table>
  <Table.Head>
    <Table.TR>
      <Table.TH>Title</Table.TH>
      <Table.TH>Year</Table.TH>
      <Table.TH>Rating</Table.TH>
      <Table.TH>Description</Table.TH>
    </Table.TR>
  </Table.Head>
  <Table.Body>
  {
    movieData && movieData.map(post => (
            <Table.TR key={post._id}>
      <Table.TH >{post.title}</Table.TH>
      <Table.TH>{post.year}</Table.TH>
      <Table.TH>{post.rating}</Table.TH>
      <Table.TH>{post.description}</Table.TH>
      <Table.TH> <StyledButton buttonLabel="DELETE X" backgroundColor="violet" 
              onClick={() =>{ dispatch(deleteMovie({id:post._id}))}}>{""}
                </StyledButton></Table.TH>
    </Table.TR>          
          ))}
  </Table.Body>
</Table>
    </div>
  )
}
export default DeleteMovie
