const DeleteMovie = () => {

  const dispatch = useDispatch();

  const  { movieData, loading, error } = useSelector((state) =>  state.fetchMoviesReducer);

  useEffect(() => {

   dispatch( fetchMovie());

}, [dispatch]);

  return (

    <div>


     DELETE
