        {loading && <div>LOADING...</div>}
        {error && <div>{error}</div>}
      <ul>
          {
          movieData && movieData.map(post => (
              <div>
            <li key={post._id} >title {post.title} rating {post.rating}  year {post.year} decription {post.decription}

            </li>
            <Button backgroundColor="yellow" onClick={() =>{ dispatch(deleteMovie({id:post._id}))          
        }}>
          X
              </Button>
            </div>


          ))}
        </ul>
      </div>
    </div>
  )
}
export default DeleteMovie
