import React, { useEffect } from "react"
import { StyledButton}  from "../components/styled/Button.style";
import { Table } from "../components/Table";
import { Input } from "./styled/Input.style";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useDispatch } from "react-redux";
import {toggleEdit,updateMovie} from '../redux/action-creators';


  const UpdateMovies = ({movieDataOne}) =>{

    console.log("UPDATE MOVIE",movieDataOne)

    const dispatch = useDispatch();
    const history = useHistory();
    const schema = yup.object().shape({
        id: yup.string(),
        title: yup.string().required(),
        year: yup.number().min(1000).max(9999).typeError("Must be a number"),
        rating: yup.number().min(1).max(10).typeError("Must be a number"),
        description: yup.string(),
      })

      const {
        register,
        handleSubmit,
        formState: { errors },
        setValue,
        reset,
      } = useForm({ resolver: yupResolver(schema)
      });

      const onSubmit =  (d) =>{
        console.log("d",d.id);
         dispatch(updateMovie(d));
         dispatch(toggleEdit({id:d.id}));  

      }

      useEffect(()=>{
          if(movieDataOne){
            setValue('fieldArray',[

                {
                title:movieDataOne.title
            },
            {
                year:movieDataOne.year
            },
            {
                rating:movieDataOne.rating
            },
            {
                description:movieDataOne.description
            },

        ])
          }
      },[movieDataOne])


return (
    <div>
   <Table>
    <Table.Head>
    <Table.TR>
 <Table.TH>Title</Table.TH>
 <Table.TH>Year</Table.TH>
 <Table.TH>Rating</Table.TH>
 <Table.TH>Description</Table.TH>
 <Table.TH>UPDATE</Table.TH>
 <Table.TH>Cancel</Table.TH>

</Table.TR>
</Table.Head>
</Table>
   {movieDataOne && movieDataOne._id && movieDataOne.title && 
       <form onSubmit={handleSubmit(onSubmit)}>       
         <Table>
  <Table.Body>
    <Table.TR key={movieDataOne._id}>
    <Input type="hidden" {...register("id")} 
    // defaultValue={movieDataOne._id}

    />
  <Table.TH > <Input   {...register("title")} 
  // defaultValue={movieDataOne.title}
  />
  <p>{errors.title?.message}</p></Table.TH>

  <Table.TH > <Input   {...register("year")} 
  // defaultValue={movieDataOne.year}
  />
  <p>{errors.year?.message}</p></Table.TH>

  <Table.TH >  <Input  {...register("rating")} 
  //  defaultValue={movieDataOne.rating}
   />
  {errors.rating?.message}</Table.TH>

  <Table.TH > <Input   {...register("description")} 
  // defaultValue={movieDataOne.description}
  />
  <p>{errors.description?.message}</p></Table.TH>

 <Table.TH > <StyledButton type='submit' buttonLabel="✓"  backgroundColor="#01bf41"  backgroundColorOnHover="green"   
         ></StyledButton></Table.TH>    
         <Table.TH> <StyledButton buttonLabel="X"  backgroundColor="#e50d0d" 
              onClick={() => history.go(0) }>{""}
                </StyledButton> 
                </Table.TH> 
            </Table.TR> 

            </Table.Body>           
              </Table>              
              </form>
}
</div>

)

}


export default UpdateMovies
