import axios from "axios";
import {
    SIGN_IN_FAILURE,
    SIGN_IN_REQUEST,
    SIGN_IN_SUCCESS,
    SIGN_UP_FAILURE,
    SIGN_UP_REQUEST,
    SIGN_UP_SUCCESS,
    SIGN_OUT_FAILURE,
    SIGN_OUT_REQUEST,
    SIGN_OUT_SUCCESS,

    FETCH_MOVIES_SUCCESS,
    FETCH_MOVIES_FAILURE,
    FETCH_MOVIES_REQUEST,

    CREATE_MOVIES_SUCCESS,
    CREATE_MOVIES_FAILURE,
    CREATE_MOVIES_REQUEST
} from './action-types';

import { URL } from "../components/URL";

// Sign up action creators

const signUpRequest = () =>{
    return{
        type: SIGN_UP_REQUEST,
    };
};

const signUpSuccess = (user) =>{
    return{
        type: SIGN_UP_SUCCESS,
        payload: {
            user
        }
    };
};

const signUpFailure = (error) =>{
    return {
        type: SIGN_UP_FAILURE,
        payload:error
    };
};

export const signUp = (user,history) =>{
    return function (dispatch) {
        dispatch(signUpRequest());
        axios({
            method:"post",
            url: URL+'/signUp',
            data: user,
        })
        .then((response)=>{
            const {data} = response.data;
            dispatch(signUpSuccess(data));
            history.push("/")
        })
        .catch((error)=>{
            console.log(error);
            dispatch(signUpFailure(error));
        });
    };
};

//Sign in action creators

const signInRequest = () =>{
    return {
        type: SIGN_IN_REQUEST
    };
};

const signInSuccess = (token) =>{
    return {
        type: SIGN_IN_SUCCESS,
        payload:{
            token
        }
    };
};

const signInFailure = (error) =>{
    return {
        type: SIGN_IN_FAILURE,
        payload: error,
    };
};

export const signIn = (payload,history) =>{
    return function (dispatch) {
        dispatch(signInRequest);
        axios({
            method:"POST",
            url: URL+"/signIn",
            data:payload,
            headers:{
                "Content-type":"application/json",
                "Accept":"application/json",
                Authorization:`Bearer ${localStorage.getItem("USER-TOKEN")}`
            }
        })
        .then((response)=>{
            const {token} = response.data;
            localStorage.setItem("USER-TOKEN",token);
            dispatch(signInSuccess(token));
            history.push("/home");
        })
        .catch((error)=>{
            dispatch(signInFailure(error));
        });
    };
};

// sign out action creators

export const signOutRequest = () =>{
    return {
        type: SIGN_OUT_REQUEST,
    };
};

export const signOutSuccess = () =>{
    return {
      type: SIGN_OUT_SUCCESS,
    };
  };

export const signOutFailure = () =>{
    return {
        type: SIGN_OUT_FAILURE
    };
};

export const signOut = (history) =>{
    return function(dispatch){
        dispatch(signOutRequest());
        localStorage.clear();
        history.push('/signin');
        if(localStorage.getItem("USER-TOKEN")){
            dispatch(signOutFailure());
        }
        else{
            dispatch(signOutSuccess());
        }
    };
}


// fetch movies action creators

const fetchMoviesRequest = () =>{
    return{
        type: FETCH_MOVIES_REQUEST,
        payload: {
            loading: true
          }
    };
};

const fetchMoviesSuccess = (movieData) =>{

    return{
        type: FETCH_MOVIES_SUCCESS,
        payload: {
            movieData
        }
    };
};

const fetchMoviesFailure = (error) =>{
    return {
        type: FETCH_MOVIES_FAILURE,
        payload:error
    };
};

export const fetchMovie = (payload) =>{
    console.log("hello");
    return async function (dispatch) {
        dispatch(fetchMoviesRequest());
        try{
            const res = await axios({
                    method:"get",
                    url: URL+'/getallmovies',
                    data: payload,
                });
                const result = res.data;
                console.log("result",result);
                dispatch(fetchMoviesSuccess(result));
        }catch(err){
            console.log("error",err);
                dispatch(fetchMoviesFailure(err));
        }
    };
};

const createMovieRequest = () =>{
    return{
        type: CREATE_MOVIES_REQUEST,
    };
};

const createMovieSuccess = (createMovieData) =>{
    return{
        type: CREATE_MOVIES_SUCCESS,
        payload: {
            createMovieData
        }
    };
};

const createMovieFailure = (error) =>{
    return {
        type: CREATE_MOVIES_FAILURE,
        payload:error
    };
};

export const createMovie = (createMovieData,history) =>{
    return function (dispatch) {
        dispatch(createMovieRequest());
        axios({
            method:"post",
            url: URL+'/addmovie',
            data: createMovieData,
        })
        .then((response)=>{
            const {data} = response.data;
            dispatch(createMovieSuccess(data));
            history.push("/")
        })
        .catch((error)=>{
            console.log(error);
            dispatch(createMovieFailure(error));
        });
    };
};
