<div>
   <Table>
    <Table.Head>
    <Table.TR>
 <Table.TH>Title</Table.TH>
 <Table.TH>Year</Table.TH>
 <Table.TH>Rating</Table.TH>
 <Table.TH>Description</Table.TH>
 <Table.TH>Delete</Table.TH>

</Table.TR>
</Table.Head>
</Table>


{
  movieData && movieData.map(post => (


       <form onSubmit={handleSubmit(onSubmit)}>

         <Table>


  <Table.Body>
    <Table.TR key={post._id}>
  <Table.TH > {edit ? <input   {...register("id")} placeholder={post._id}/> : ''}</Table.TH>
  <Table.TH > <input   {...register("title")} placeholder={post.title}/></Table.TH>
  <Table.TH > <input   {...register("year")} placeholder={post.year}/></Table.TH>
  <Table.TH >  <input  {...register("rating")}  placeholder={post.rating}/></Table.TH>
  <Table.TH > <input   {...register("description")} placeholder={post.description}/></Table.TH>
  <Table.TH >  <Input buttonLabel="✅" type="submit" value="UPDATE"
           ></Input></Table.TH>

            </Table.TR> 
            </Table.Body>  

              </Table>   

              </form>


        ))}
</div>

}

<StyledButton buttonLabel="✅"  backgroundColorOnHover="#01bf41"
 onClick={()=>
          dispatch(toggleEdit())
        } >EDIT</StyledButton>


    </div>
  )
}
export default DeleteMovie
